#include "ConcreteMediator.h"

int ConcreteMediator::connect_colleage ( Colleage* a, Colleage* b )
{
    _colleage_a = a;
    _colleage_b = b;
}

void ConcreteMediator::set_colleage_a ( Colleage* a )
{
    _colleage_a = a;
}

void ConcreteMediator::set_colleage_b ( Colleage* b )
{
    _colleage_b = b;
}

Colleage* ConcreteMediator::get_colleage_a()
{
    return _colleage_a;
}

