{
    return _colleage_b;
}

ConcreteMediator::ConcreteMediator () : Mediator()
{

}

void ConcreteMediator::doActionFromAToB()
{
   _colleage_b->set_state( _colleage_a->get_state() );
}

void ConcreteMediator::doActoinFromBToA()
{
	_colleage_a->set_state( _colleage_b->get_state() );
}